package ex1;

public class 명명법 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("-절대불가-");
		System.out.println("식별자는클래스명, 변수명, 메소드명, 상수명 등을 이야기 한다.");
		System.out.println("첫 글자가 숫자	ex. int 1kor;");
		System.out.println("중간에 공백이 들어가면  안됨.");
		System.out.println("특수문자 사용 불가(키보드의 영문, 숫자, 한글, _, $를 제외한 모든 문자.)");
		System.out.println("예약어 사용불가(=미리 만들어놓은 명령어 사용불가.)");
		System.out.println("\n\n");
		System.out.println("** 클래스명 명명 추천사항");
		System.out.println("첫글자 대문자.");
		System.out.println("두 단어 이상이 되면 매 단어 첫글자는 대문자. ex. OldProduct");
		System.out.println("\n\n");
		System.out.println("** 메소드, 변수명 명명법");
		System.out.println("첫글자는 소문자로 기록. ");
		System.out.println("두 단어 이상이 되면 매 단어 첫글자는 대문자 ex. OldProduct");
		System.out.println("두 단어 이상이 되면 연결은 _이용. ex. week_pay");
		System.out.println("\n\n");
		System.out.println("** 상수 명명법(=변하지 않는 값)");
		System.out.println("모두 대문자");
		System.out.println("두 단어 이상이 되면 연결은 _이용. ex. WEEK_PAY");
		
	}
}
